/**
 * Cancel orders
 *
 * @package     selenium
 * @subpackage  tests
 * @license     http://opensource.org/licenses/osl-3.0.php  Open Software License (OSL 3.0)
 */
class Core_Mage_Order_Cancel extends Mage_Selenium_TestCase
{

    /**
     * <p>Preconditions:</p>
     *
     * <p>Log in to Backend.</p>
     */
    public function setUpBeforeTests()
    {
        $this->loginAdminUser();
    }

    protected function assertPreConditions()
    {
        $this->addParameter('id', '0');
    }

    /**
     * Create Simple Product for tests
     *
     * @test
     */
    public function createSimpleProduct()
    {
        //Data
        $productData = $this->loadData('simple_product_for_order', NULL, array('general_name', 'general_sku'));
        //Steps
        $this->navigate('manage_products');
        $this->assertTrue($this->checkCurrentPage('manage_products'), $this->messages);
        $this->productHelper()->createProduct($productData);
        //Verifying
        $this->assertTrue($this->successMessage('success_saved_product'), $this->messages);

        return $productData['general_sku'];
    }

    /**
     * Cancel Pending Order From Order Page
     *
     * @depends createSimpleProduct
     * @dataProvider dataPaymentMethods
     * @test
     */
    public function cancelPendingOrderFromOrderPage($payment, $simpleSku)
    {
        //Data
        $orderData = $this->loadData('order_newcustomer_' . $payment . '_flatrate', array('filter_sku' => $simpleSku));
        //Steps
        $this->navigate('system_configuration');
        if ($payment != 'checkmoney') {
            $payment .= '_without_3Dsecure';
        }
        $this->systemConfigurationHelper()->configure($payment);
        $this->navigate('manage_sales_orders');
        $this->orderHelper()->createOrder($orderData);
        //Verifying
        $this->assertTrue($this->successMessage('success_created_order'), $this->messages);
        //Steps
        $this->clickButtonAndConfirm('cancel', 'confirmation_for_cancel');
        //Verifying
        $this->assertTrue($this->successMessage('success_canceled_order'), $this->messages);
    }

    public function dataPaymentMethods()
    {
        return array(
            array('savedcc'),
            array('checkmoney'),
            array('authorizenet')
        );
    }

}
